<?php
function get_permutations($arr) {
    $n = count($arr);
    if ($n <= 1) {
        return array($arr);
    } else {
        $perms = array();
        $first = $arr[0];
        $subarr = array_slice($arr, 1);
        $subperms = get_permutations($subarr);
        foreach ($subperms as $subperm) {
            for ($i = 0; $i <= count($subperm); $i++) {
                $newperm = array_merge(
                    array_slice($subperm, 0, $i),
                    array($first),
                    array_slice($subperm, $i)
                );
                $perms[] = $newperm;
            }
        }
        return $perms;
    }
}

function check_string($str) {
    return !preg_match('/ti/', $str);
}

function filter_permutations($perms) {
    $filtered_perms = array();
    foreach ($perms as $perm) {
        $str = implode('', $perm);
        if (check_string($str)) {
            if ($str[0] != 'c' || substr($str, -2) != 'ti') {
                $filtered_perms[] = strrev($str);
            }
        }
    }
    return $filtered_perms;
}

