```c++
#include <Adafruit_NeoPixel.h>

#define PIXEL_PIN 6
#define PIXEL_COUNT 27

Adafruit_NeoPixel pixels(PIXEL_COUNT, PIXEL_PIN, NEO_GRB + NEO_KHZ800);

int blueScore = 0;
int redScore = 0;

int invasionIndex = -1;
bool invasionInProgress = false;

const int buttonPin = 2;
int buttonState = 0;

void setup() {
  pixels.begin();
  pixels.setBrightness(50);
  pixels.show();

  pinMode(buttonPin, INPUT);
}

void loop() {
  buttonState = digitalRead(buttonPin);

  if (buttonState == HIGH && !invasionInProgress) {
    invasionInProgress = true;
    invadeRedPixel();
  }

  if (invasionInProgress) {
    pixels.clear();

    for (int i = 0; i < PIXEL_COUNT; i++) {
      if (i < (PIXEL_COUNT / 2)) {
        pixels.setPixelColor(i, 0, 0, 255);
      } else if (i == invasionIndex) {
        pixels.setPixelColor(i, 255, 0, 0);
      } else {
        pixels.setPixelColor(i, 255, 0, 0);
      }
    }

    pixels.show();
    delay(100);

    if (invasionIndex == -1) {
      invasionInProgress = false;
    }
  }
}

void invadeRedPixel() {
  int invasionPosition = random((PIXEL_COUNT / 2), PIXEL_COUNT);

  for (int i = 0; i < PIXEL_COUNT / 2; i++) {
    pixels.setPixelColor(i, 0, 0, 255);
  }

  pixels.setPixelColor(invasionPosition, 255, 0, 0);
  pixels.show();

  invasionIndex = invasionPosition;

  delay(1000);

  if (invasionIndex != -1) {
    blueScore++;
    invasionIndex = -1;
    invasionInProgress = false;
  }
}
```

